mapper.update:
UPDATE bbs SET subject=#{subject}, content=#{content},
		saveFilename=#{saveFilename, jdbcType=VARCHAR},
		originalFilename=#{originalFilename, jdbcType=VARCHAR}
WHERE num = #{num}

mapper.delete:
DELETE FROM bbsReply
WHERE replyNum=#{replyNum}

	OR answer=#{replyNum}

mapper.select:
SELECT replyNum, num, r.userId, userName, content, reg_date, answer, NVL(showReply, 1) showReply FROM bbsReply r JOIN member1 m ON r.userId = m.userId WHERE answer = #{answer}
AND ( showReply=1 OR ( showReply=0 AND r.userId=#{userId} ) )
ORDER BY replyNum DESC

SELECT COUNT(DECODE(replyLike, 1, 1)) likeCount,
	   COUNT(DECODE(replyLike, 0, 1)) disLikeCount
FROM bbsReplyLike WHERE replyNum = #{replyNum}
