mapper.select:
SELECT num id, subject title, sday, eday, stime, etime,
		s.categoryNum, category, color, repeat, repeat_cycle, memo, reg_date
FROM schedule s
LEFT OUTER JOIN scheduleCategory sc ON s.categoryNum = sc.categoryNum

	s.userId = #{userId}

		AND 
		s.categoryNum IN

			#{categoryNum}


	AND
	( 
		( 
			 ( TO_DATE(sday, 'YYYY-MM-DD') >= TO_DATE(#{start}, 'YYYY-MM-DD')
				AND TO_DATE(sday, 'YYYY-MM-DD') <= TO_DATE(#{end}, 'YYYY-MM-DD')
			 ) OR ( TO_DATE(eday, 'YYYY-MM-DD') <= TO_DATE(#{start}, 'YYYY-MM-DD') 
			     AND TO_DATE(eday, 'YYYY-MM-DD') <= TO_DATE(#{end}, 'YYYY-MM-DD')
			 )
		) OR (
			repeat=1 AND repeat_cycle!=0
				AND ADD_MONTHS(TO_DATE(sday, 'YYYY-MM-DD'), 12 * repeat_cycle*TRUNC(((SUBSTR(#{start},1,4)-SUBSTR(sday,1,4))/repeat_cycle))) >= TO_DATE(#{start}, 'YYYY-MM-DD')
				AND ADD_MONTHS(TO_DATE(sday, 'YYYY-MM-DD'), 12 * repeat_cycle*TRUNC(((SUBSTR(#{start},1,4)-SUBSTR(sday,1,4))/repeat_cycle))) <= TO_DATE(#{end}, 'YYYY-MM-DD')
	  )
	)

ORDER BY sday ASC, id DESC
